import { useState } from "react";
import UserList from "./components/UserList";
import AddUser from "./components/AddUser";

export default function App() {
  const [reloadKey, setReloadKey] = useState(0);
  const [editUser, setEditUser] = useState(null);

  const handleEdit = (user) => {
    setEditUser(user);
  };

  const handleCancelEdit = () => {
    setEditUser(null);
  };

  const handleAdded = () => {
    setReloadKey((k) => k + 1);
    setEditUser(null);
  };

  return (
    <div className="app-shell">
      <header className="app-header">
        <div className="app-brand">
          <div className="app-logo" aria-hidden>UA</div>
          <div className="app-title">
            <h1>Users Admin</h1>
            <p className="app-subtitle">CRUD mẫu với MongoDB · Express · React</p>
          </div>
        </div>
        <span className="app-badge">Demo</span>
      </header>

      <main className="app-main">
        <section className="panel panel--form">
          <div className="panel__header">
            <h2 className="panel__title">{editUser ? "Sửa user" : "Thêm user"}</h2>
            {editUser && <span className="tag">Đang sửa</span>}
          </div>
          <div className="panel__body">
            <AddUser
              onAdded={handleAdded}
              editUser={editUser}
              onCancelEdit={handleCancelEdit}
            />
          </div>
        </section>

        <section className="panel panel--list">
          <div className="panel__header">
            <h2 className="panel__title">Danh sách users</h2>
          </div>
          <div className="panel__body">
            <UserList key={reloadKey} onEdit={handleEdit} />
          </div>
        </section>
      </main>
    </div>
  );
}
